import React, { useState, useEffect } from 'react';
import RoomCard from '../components/RoomCard';

const Rooms = () => {
  const [rooms, setRooms] = useState([]);
  useEffect(() => {
    const room = [
      {
        id: 1,
        name: "Deluxe Room",
        price: 5000,
        description: "A luxurious room with all modern amenities.",
        image: "data:image/jpeg;base64,",
        amenities: ["Free Wi-Fi", "Air Conditioning", "Flat Screen TV", "Mini Bar"],
        capacity: 2,
        availability: true,
      },
      {
        id: 2,
        name: "Standard Room",
        price: 3000,
        description: "A comfortable room for budget travelers.",
        image: "data:image/jpeg;base64,",
        amenities: ["Free Wi-Fi", "Air Conditioning"],
        capacity: 2,
        availability: true,
      },
      {
        id: 3,
        name: "Family Suite",
        price: 8000,
        description: "A spacious suite perfect for families.",
        image: "data:image/jpeg;base64,",
        amenities: ["Free Wi-Fi", "Air Conditioning", "Kitchenette", "Dining Area"],
        capacity: 4,
        availability: false,
      },
    ];

    setRooms(room); // Set the state inside useEffect
  }, []); // Empty dependency array ensures this runs only once


  return (
    <div>
      <h1 style={headingStyles}>Our Rooms</h1>
      <div style={galleryStyles}>
        {rooms.map(room => (
          <RoomCard key={room.id} {...room} />
        ))}
      </div>
    </div>
  );
};


// Simple styling for gallery and images
const galleryStyles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', // Creates a responsive grid layout
    gap: '20px',
    margin: '20px',
  };
  
  const headingStyles = {
    textAlign: 'center',
    fontSize: '2rem',
    color: '#333',
    marginBottom: '30px',
  };
 
  
export default Rooms;
